package com.collaborativeeclipse.cct.java.listeners;

import org.eclipse.swt.events.SelectionEvent;
import org.eclipse.swt.events.SelectionListener;
import org.eclipse.swt.widgets.Dialog;

public abstract class TransmissionSelectionListener implements SelectionListener {

	private Dialog owner = null;
	
	public TransmissionSelectionListener(Dialog dialog) {
		owner = dialog;
	}
	@Override
	public void widgetDefaultSelected(SelectionEvent e) {
		// TODO Auto-generated method stub
		
	}

	@Override
	public void widgetSelected(SelectionEvent e) {
		// TODO Auto-generated method stub
		
	}
	
	public Dialog getOwner() {
		return owner;
	}
}
